const { RichEmbed } = require("discord.js");
function titleCase(str) {
	str = str.toLowerCase();
	str = str.split(" ");
	for (var i = 0; i < str.length; i++) {
		str[i] = str[i].charAt(0).toUpperCase() + str[i].slice(1);
	}
	return str.join(" ");
}
const MENU =  [
	"mac and cheese",
	"chicken nuggets",
];
module.exports = {
	menu: MENU,
	menuEmbed: new RichEmbed()
		.setColor("RED")
		.setTitle("Menu")
		.setDescription(MENU.map((m) => titleCase(m)).join("\n")),
}
